package com.midterm.photonotes.yuanzhiqing;import android.os.Bundle;import android.widget.Button;import android.widget.EditText;import android.widget.ImageView;import android.widget.Toast;import androidx.appcompat.app.AppCompatActivity;public class NoteActivity extends AppCompatActivity{ImageView iv;EditText etT,etB;@Override protected void onCreate(Bundle s){super.onCreate(s);setContentView(R.layout.activity_note);iv=findViewById(R.id.ivPhoto);etT=findViewById(R.id.etTitle);etB=findViewById(R.id.etBody);Button bAttach=findViewById(R.id.btnAttach);Button bSave=findViewById(R.id.btnSave);bAttach.setOnClickListener(v->iv.setImageResource(R.drawable.WechatIMG2738));bSave.setOnClickListener(v->{Toast.makeText(this,"已保存（演示）",Toast.LENGTH_SHORT).show();finish();});}}