package com.example.profile.nieyuhang;import android.os.Bundle;import android.widget.TextView;import androidx.appcompat.app.AppCompatActivity;public class ProfileActivity extends AppCompatActivity{DBHelper db; @Override protected void onCreate(Bundle s){super.onCreate(s); setContentView(R.layout.activity_profile); db=new DBHelper(this); String user=getIntent().getStringExtra("username"); if(user==null) user=""; String[] c=db.getUserContact(user); ((TextView)findViewById(R.id.tvUser)).setText(user); ((TextView)findViewById(R.id.tvEmail)).setText(c[0]); ((TextView)findViewById(R.id.tvPhone)).setText(c[1]); }}