package com.example.profile.nieyuhang;import android.os.Bundle;import android.widget.Button;import android.widget.EditText;import android.widget.Toast;import androidx.appcompat.app.AppCompatActivity;public class RegisterActivity extends AppCompatActivity{DBHelper db;@Override protected void onCreate(Bundle s){super.onCreate(s);setContentView(R.layout.activity_register);db=new DBHelper(this);EditText u=findViewById(R.id.regUser); EditText p=findViewById(R.id.regPass); EditText e=findViewById(R.id.regEmail); EditText ph=findViewById(R.id.regPhone); Button b=findViewById(R.id.btnDoRegister); b.setOnClickListener(v->{ String uu=u.getText().toString().trim(); String pp=p.getText().toString().trim(); if(uu.isEmpty()||pp.isEmpty()){Toast.makeText(this,"用户名/密码不能为空",Toast.LENGTH_SHORT).show();return;} boolean ok=db.addUser(uu,pp,e.getText().toString().trim(),ph.getText().toString().trim()); if(ok){Toast.makeText(this,"注册成功",Toast.LENGTH_SHORT).show();finish();}else{Toast.makeText(this,"注册失败",Toast.LENGTH_SHORT).show();}});}}