package org.midterm.quiz.chenxiaotin;import android.content.Intent;import android.os.Bundle;import android.widget.Button;import android.widget.RadioButton;import android.widget.RadioGroup;import android.widget.TextView;import androidx.appcompat.app.AppCompatActivity;public class QuizActivity extends AppCompatActivity{int q=0,score=0;String[] qs={"2+3= ?","Capital of China?","Android by?"};String[][] opts={{"4","5","6"},{"Beijing","Shanghai","Guangzhou"},{"Apple","Google","Microsoft"}};int[] ans={1,0,1};TextView tv;RadioGroup rg;Button btn;@Override protected void onCreate(Bundle s){super.onCreate(s);setContentView(R.layout.activity_quiz);tv=findViewById(R.id.tvQ);rg=findViewById(R.id.rg);btn=findViewById(R.id.btnNext);load();btn.setOnClickListener(v->{int sel=-1;for(int i=0;i<rg.getChildCount();i++) if(((RadioButton)rg.getChildAt(i)).isChecked()) sel=i; if(sel==-1) return; if(sel==ans[q]) score++; q++; if(q>=qs.length){ Intent i=new Intent(this,ResultActivity.class); i.putExtra("score",score); startActivity(i); finish(); } else load();});} void load(){ tv.setText(qs[q]); for(int i=0;i<3;i++){ ((RadioButton)rg.getChildAt(i)).setText(opts[q][i]); ((RadioButton)rg.getChildAt(i)).setChecked(false); } }}